#include <iostream>#include <string>using namespace std;string NgayQT(string s);#pragma warning( disable : 4996 )int main(){	string s;	cout << "Nhap ngay/thang/nam: ";	getline(cin, s);	cout << "Dang ngay quoc te: " << NgayQT(s) << endl;	return 0;}string NgayQT(string s){	string months[] = { "Jannuary", "February", "March", "April", "May, June", "July", "August", "September", "October", "November", "December" };	int pos = s.find("/");// tim ki tu / dau tien	int thang = atoi(s.substr(0, pos).c_str());//lay chuoi truoc /, chuyen ve Cstring, chuyen ve dang so	s = s.substr(pos + 1); // bo phan dau va ki tu /	pos = s.find("/");	int ngay = atoi(s.substr(0, pos).c_str());//lay chuoi truoc /, chuyen ve Cstring, chuyen ve dang so	int nam = atoi(s.substr(pos + 1).c_str());//lay chuoi sau /, chuyen ve Cstring, chuyen ve dang so	return months[thang - 1] + " " + to_string(ngay) + ", " + to_string(nam);}